import Cart from "./components/Cart/Cart";
import Layout from "./components/Layout/Layout";
import Products from "./components/Shop/Products";
import { useSelector, useDispatch } from "react-redux";
import { useEffect } from "react";
import { uiActions } from "./store/ui-slice";
import React from "react";
import Notification from "./components/UI/Notification";

let initialStartApp = true;
function App() {
  const dispatch = useDispatch();
  const isShowCart = useSelector((state) => state.ui.isShowCart);
  const cart = useSelector((state) => state.cart);
  const notification = useSelector((state) => state.ui.showNotification);

  useEffect(() => {
    const sendCartData = async () => {
      dispatch(
        uiActions.showNotification({
          status: "pending",
          title: "Sending...",
          message: "Sending request...",
        })
      );
      const response = await fetch(
        "https://react-http-56668-default-rtdb.asia-southeast1.firebasedatabase.app/cart.json",
        {
          method: "PUT",
          body: JSON.stringify(cart),
        }
      );

      if (!response.ok) {
        dispatch(
          uiActions.showNotification({
            status: "error",
            title: "Error",
            message: "Something went wrong when send request!",
          })
        );
      }

      dispatch(
        uiActions.showNotification({
          status: "error",
          title: "Error",
          message: "Something went wrong when send request!",
        })
      );
    };

    if (initialStartApp) {
      initialStartApp = false;
      return;
    }
    sendCartData().catch((err) => {
      dispatch(
        uiActions.showNotification({
          status: "error",
          title: "Error",
          message: "Something went wrong when send request!",
        })
      );
    });
  }, [cart, dispatch]);
  return (
    <React.Fragment>
      {notification && (
        <Notification
          status={notification.status}
          title={notification.title}
          message={notification.message}
        ></Notification>
      )}
      <Layout>
        {isShowCart && <Cart />}
        <Products />
      </Layout>
    </React.Fragment>
  );
}

export default App;
