import React, { useState } from "react";
import Card from "../UI/Card";
import classes from "./AddUser.module.css";
import Button from "../UI/Button"

const AddUser = (props) => {
    const [enterUsername, setUsername] = useState('');
    const [enterAge, setAge] = useState(0);
    const [isValid, setIsValid] = useState(true);

    const handleUsernameChange = (event) => {
        setUsername(event.target.value);
        checkIsValid();
    }

    const handleAgeChange = (event) => {
        setAge(event.target.value)
        checkIsValid();
    }

    const checkIsValid = () => {
        if(enterUsername.trim().length === 0 || enterAge <= 0){
            setIsValid(false);
        }
    }

    const handleAddUser = () => {
        if(isValid){
            var user = {
                userName: {enterUsername},
                age: {enterAge}
            }

            console.log(user.userName)
            props.onAddUser(user)
        }

    }

  return (
    <Card className={classes.input}>
      <form>
        <label htmlFor="username">Username</label>
        <input id="username" type="text" value={enterUsername} onChange={handleUsernameChange}></input>
        <label htmlFor="age">Age(Years)</label>
        <input id="age" type="number" value={enterAge} onChange={handleAgeChange}></input>
        <Button type="submit">Add User</Button>
      </form>
    </Card>
  );
};

export default AddUser;
